import './index.scss'
import { useEffect, useState } from 'react'

type PropsType = {
  resultData: string
}

const ResultBox = (props: PropsType) => {
  const [words, setWords] = useState(0)
  const [characters, setCharacters] = useState(0)
  const [sentences, setSentences] = useState(0)
  const [paragraphs, setParagraphs] = useState(0)
  const [pronouns, setPronouns] = useState(0)

  function countWords(text: string) {
    // Удаляем все пробелы в начале и конце текста
    text = text.trim()

    // Разделяем текст на отдельные слова по пробелам
    let words = text.split(' ')

    // Удаляем пустые элементы массива (в случае множественных пробелов)
    words = words.filter(function (word) {
      return word.length > 0
    })

    // Возвращаем количество слов
    return words.length
  }
  function countSentences(text: string) {
    // Разделяем текст на предложения, используя разделители [.?!]
    let sentences = text.split(/[.?!]+/)

    // Удаляем пустые элементы массива (в случае множественных разделителей)
    sentences = sentences.filter(function (sentence) {
      return sentence.length > 0
    })

    // Возвращаем количество предложений
    return sentences.length
  }

  // Пример использования
  const sentenceCount = countSentences(props.resultData)

  const wordCount = countWords(props.resultData)

  useEffect(() => {
    setCharacters(props.resultData.length)
    setWords(wordCount)
    setSentences(sentenceCount)
  }, [props])

  const resultBar = [
    {
      title: 'Words',
      value: words,
    },
    {
      title: 'Characters',
      value: characters,
    },
    {
      title: 'Sentences',
      value: sentences,
    },
    {
      title: 'Paragraphs ',
      value: paragraphs,
    },
    {
      title: 'Pronouns',
      value: pronouns,
    },
  ]

  return (
    <div className="result-bar">
      {resultBar.map(({ title, value }) => (
        <div className="result-box" key={title}>
          <span className="box-title">{title}</span>
          <span className="box-value">{value}</span>
        </div>
      ))}
    </div>
  )
}

export default ResultBox
