import {ChangeEvent, useRef} from "react";

const TextArea = () => {
  const onchangeHandler = (value:ChangeEvent<HTMLTextAreaElement>)=>{
    let valueData = value.target.value
    const refData = useRef(valueData);
  }


  return <textarea onChange={(event)=>onchangeHandler(event)} className="text-area" placeholder="Paste your text here..." />
}

export default TextArea
